CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getCantidadComentariosPublicacion`($id_publicacion_proyecto_docente int, $type INT)
BEGIN
IF($type = "1") THEN
	select count(id_publicacion_proyecto_docente)  as cantidad_comentarios_publicacion
	from (select cp.*
	from proyectos pro
	join rel_docentes_proyectos rdp on(pro.id_proyecto = rdp.id_proyecto )
	join publicaciones_proyectos pp on(rdp.id_rel_proyecto_docente = pp.id_rel_proyecto_docente)
	join comentarios_publicaciones cp on(pp.id_publicacion_proyecto_docente = cp.id_publicacion_proyecto_docente)
	join administradores ad on (cp.id_administrador = ad.id_administrador)
	where pro.estatus = '1' and rdp.estatus_docente = '1' and rdp.estatus_proyecto = '1' and ad.estatus = '1'
	and cp.id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente
	union all
	select cp.*
	from proyectos pro
	join rel_docentes_proyectos rdp on(pro.id_proyecto = rdp.id_proyecto )
	join publicaciones_proyectos pp on(rdp.id_rel_proyecto_docente = pp.id_rel_proyecto_docente)
	join comentarios_publicaciones cp on(pp.id_publicacion_proyecto_docente = cp.id_publicacion_proyecto_docente)
	join docentes doc on (cp.id_docente = doc.id_docente)
	where pro.estatus = '1' and rdp.estatus_docente = '1' and rdp.estatus_proyecto = '1' and doc.estatus = '1'
	and cp.id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente) as mytable;
ELSE
	select count(id_publicacion_modulo_docente)  as cantidad_comentarios_publicacion
	from (select cp.*
	from cat_modulos pro
	join rel_docentes_modulos rdp on(pro.id_modulo = rdp.id_modulo)
	join publicaciones_modulos pp on(rdp.id_rel_modulo_docente = pp.id_rel_modulo_docente)
	join comentarios_publicaciones_mod cp on(pp.id_publicacion_modulo_docente = cp.id_publicacion_modulo_docente)
	join administradores ad on (cp.id_administrador = ad.id_administrador)
	where pro.estatus = '1' and rdp.estatus_docente = '1' and rdp.estatus_modulo = '1' and ad.estatus = '1'
	and cp.id_publicacion_modulo_docente = $id_publicacion_proyecto_docente
	union all
	select cp.*
	from cat_modulos pro
	join rel_docentes_modulos rdp on(pro.id_modulo = rdp.id_modulo )
	join publicaciones_modulos pp on(rdp.id_rel_modulo_docente = pp.id_rel_modulo_docente)
	join comentarios_publicaciones_mod cp on(pp.id_publicacion_modulo_docente = cp.id_publicacion_modulo_docente)
	join docentes doc on (cp.id_docente = doc.id_docente)
	where pro.estatus = '1' and rdp.estatus_docente = '1' and rdp.estatus_modulo = '1' and doc.estatus = '1'
	and cp.id_publicacion_modulo_docente = $id_publicacion_proyecto_docente) as mytable;
END IF;
END ;;
