CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getCapacitaciones`()
    NO SQL
SELECT c.id_capacitacion, 
	c.nombre, 
    c.descripcion, 
    p.nombre, 
    IFNULL((SELECT COUNT(s.id_capacitacion)  FROM cap_sesiones AS s WHERE s.id_capacitacion = c.id_capacitacion AND s.estatus = 1 GROUP BY s.id_capacitacion), 0) AS numero_sesiones, 
    IF (c.estatus = 1 ,'Activo','Inactivo') AS estatus
FROM capacitaciones AS c
INNER JOIN proyectos AS p
ON c.id_proyecto = p.id_proyecto
