CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getCapSesiones`(IN `$id_capacitacion` INT)
    NO SQL
IF ($id_capacitacion = '-1') THEN
		SELECT cs.id_cap_sesion ,
			p.nombre nombre_proyecto,
			c.nombre nombre_capacitacion,
			cs.nombre nombre_cap_sesion,
            cs.descripcion descripcion_cap_sesion,
            (SELECT GROUP_CONCAT(DISTINCT n2.nombre SEPARATOR ', ') FROM rel_grados_proyectos r2 JOIN cat_grados c2 ON c2.id_grado = r2.id_grado JOIN cat_niveles_educativos n2 ON n2.id_nivel_educativo = c2.id_nivel_educativo WHERE r2.id_proyecto = p.id_proyecto) AS nivel_educativo,
            (SELECT COUNT(DISTINCT id_docente) FROM proyectos p2 JOIN rel_docentes_proyectos r2 ON r2.id_proyecto = p2.id_proyecto WHERE p2.id_proyecto = p.id_proyecto) AS total_docentes,
            (SELECT COUNT(c2.id_cap_sesion) FROM cap_sesiones_grupos c2 WHERE c2.id_cap_sesion = c.id_capacitacion) AS numero_grupos,
            cs.estatus estatus_cap_sesion
        FROM cap_sesiones AS cs
        JOIN capacitaciones AS c
        ON cs.id_capacitacion = c.id_capacitacion
        JOIN proyectos AS p
        ON p.id_proyecto = c.id_proyecto
        WHERE c.estatus = '1'
        ORDER BY c.id_capacitacion ASC;
    ELSE
		SELECT * FROM cap_sesiones WHERE id_capacitacion = $id_capacitacion;
    END IF ;;
