CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getChats`(IN $id_usuario BIGINT, IN $rol CHAR(2))
BEGIN
	IF($rol = "0") THEN
        BEGIN
			DROP TEMPORARY TABLE IF EXISTS TMP_chats;
            DROP TEMPORARY TABLE IF EXISTS TMP_chats2;
			CREATE TEMPORARY TABLE IF NOT EXISTS TMP_chats AS (
				SELECT c1.id_comentario, 
					c1.comentario, 
					c1.id_docente_emisor, 
					c1.id_administrador_emisor, 
					c1.rol_emisor,
					c1.id_docente_receptor,
					c1.id_administrador_receptor,
					c1.rol_receptor,
					c1.fecha_comentario,
					c1.visto_receptor,
					c2.cantidad
				FROM chat_comentarios as c1
				JOIN(
						SELECT chat, max(id_comentario) id_comentario, SUM(IF(visto_receptor = '1' AND IF(ISNULL(id_docente_emisor), 1, id_docente_emisor != $id_usuario ), 1, 0)) as cantidad
						FROM chat_comentarios 
						WHERE ( (id_docente_emisor = $id_usuario AND rol_emisor = $rol) OR (id_docente_receptor = $id_usuario AND rol_receptor = $rol) ) AND estatus_emisor = "1"
						group by chat
					) AS c2
				ON c1.chat = c2.chat AND c1.id_comentario = c2.id_comentario
			);

			CREATE TEMPORARY TABLE IF NOT EXISTS TMP_chats2 AS (
				SELECT c1.id_comentario, 
						c1.comentario, 
						d.nombre_docente as nombre, 
						d.ape_paterno, 
						d.ape_materno,
						d.nombre_usuario,
						d.imagen,
                        d.rol,
						c1.fecha_comentario,
						c1.visto_receptor,
						c1.cantidad
				FROM TMP_chats c1
				JOIN docentes as d
				ON (d.id_docente = c1.id_docente_emisor AND c1.id_docente_emisor != $id_usuario) OR (d.id_docente = c1.id_docente_receptor AND c1.id_docente_receptor != $id_usuario)
			);

			INSERT INTO TMP_chats2 (
				SELECT c1.id_comentario, 
						c1.comentario, 
						a.nombre,
						a.ape_paterno,
						a.ape_materno,
						a.nombre_usuario,
						a.imagen,
                        a.rol,
						c1.fecha_comentario,
						c1.visto_receptor,
						c1.cantidad
				FROM TMP_chats c1
				JOIN administradores as a
				ON (a.id_administrador = c1.id_administrador_emisor) OR (a.id_administrador = c1.id_administrador_receptor)
			);
            
			SELECT id_comentario,
					comentario,
                    nombre,
                    ape_paterno,
                    ape_materno,
                    nombre_usuario,
                    imagen,
                    rol,
                    fecha_comentario,
                    F_getInfoFechaChat(fecha_comentario) as dia,
                    if(cantidad>0,"nuevo","") as css_visto_receptor,
                    cantidad
            FROM TMP_chats2 ORDER BY fecha_comentario DESC;
            
        END;
	ELSE
		BEGIN
			DROP TEMPORARY TABLE IF EXISTS TMP_chats;
			DROP TEMPORARY TABLE IF EXISTS TMP_chats2;
			CREATE TEMPORARY TABLE IF NOT EXISTS TMP_chats AS (
				SELECT c1.id_comentario, 
						c1.comentario, 
						c1.id_docente_emisor, 
						c1.id_administrador_emisor, 
						c1.rol_emisor,
						c1.id_docente_receptor,
						c1.id_administrador_receptor,
						c1.rol_receptor,
						c1.fecha_comentario,
						c1.visto_receptor,
						c2.cantidad
					FROM chat_comentarios as c1
					JOIN(
							SELECT chat, max(id_comentario) id_comentario, SUM(IF(visto_receptor = '1' AND IF(ISNULL(id_administrador_emisor), 1, id_administrador_emisor != $id_usuario ), 1, 0)) as cantidad
							FROM chat_comentarios 
							WHERE ( (id_administrador_emisor = $id_usuario AND rol_emisor = $rol) OR (id_administrador_receptor = $id_usuario AND rol_receptor = $rol) ) AND estatus_emisor = "1"
							group by chat
						) AS c2
					ON c1.chat = c2.chat AND c1.id_comentario = c2.id_comentario
			);

			CREATE TEMPORARY TABLE IF NOT EXISTS TMP_chats2 AS (
				SELECT c1.id_comentario, 
							c1.comentario, 
							d.nombre_docente as nombre, 
							d.ape_paterno, 
							d.ape_materno,
							d.nombre_usuario,
							d.imagen,
							d.rol,
							c1.fecha_comentario,
							c1.visto_receptor,
							c1.cantidad
					FROM TMP_chats c1
					JOIN docentes as d
					ON (d.id_docente = c1.id_docente_emisor) OR (d.id_docente = c1.id_docente_receptor)
			);

			INSERT INTO TMP_chats2 (
				SELECT c1.id_comentario, 
						c1.comentario, 
						a.nombre,
						a.ape_paterno,
						a.ape_materno,
						a.nombre_usuario,
						a.imagen,
						a.rol,
						c1.fecha_comentario,
						c1.visto_receptor,
						c1.cantidad
				FROM TMP_chats c1
				JOIN administradores as a
				ON (a.id_administrador = c1.id_administrador_emisor AND c1.id_administrador_emisor != $id_usuario) OR (a.id_administrador = c1.id_administrador_receptor AND c1.id_administrador_receptor != $id_usuario)
			);	  
				
			SELECT id_comentario,
					comentario,
					nombre,
					ape_paterno,
					ape_materno,
					nombre_usuario,
					imagen,
					rol,
					fecha_comentario,
                    F_getInfoFechaChat(fecha_comentario) as dia,
					if(cantidad>0,"nuevo","") as css_visto_receptor,
					cantidad
			FROM TMP_chats2 ORDER BY fecha_comentario DESC;
		END;
	END IF;
END ;;
DELIMITER ;
