CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getDocentesModulosRelacionados`(IN `$url` VARCHAR(250), IN $id_ciclo_escolar INT, IN `$type` INT)
    NO SQL
BEGIN

	IF($type = 0) THEN
		set @id_modulo = (select id_modulo from rel_docentes_modulos where url = $url);
	ELSE
		set @id_modulo = (select id_modulo from cat_modulos where url = $url);
	END IF;
    
    select distinct m.id_modulo,doc.id_docente,doc.nombre_docente,doc.ape_paterno,doc.ape_materno,doc.imagen,doc.nombre_usuario,
	case (select  count(distinct doc2.id_docente,rapd2.url)  
	from docentes doc2
	join rel_docentes_modulos rapd2 on(doc2.id_docente = rapd2.id_docente)
	join cat_escuelas esc2 on(rapd2.clave_escuela = esc2.clave_escuela)
	join cat_grados gra2 on(rapd2.id_grado=gra2.id_grado)
	join cat_grupos gru2 on(rapd2.id_grupo=gru2.id_grupo)
	join cat_modulos m2 on(rapd2.id_modulo = m2.id_modulo)
	where rapd2.id_docente = rapd.id_docente  and rapd2.id_modulo = rapd.id_modulo and rapd2.estatus_docente = '1' 
	and rapd2.estatus_modulo = '1' and rapd2.id_ciclo_escolar = $id_ciclo_escolar) 
	when 1 then rapd.url
	else
	''
	end as url
	from cat_modulos m
	join rel_docentes_modulos rapd on(m.id_modulo=rapd.id_modulo )
	join docentes doc on(rapd.id_docente = doc.id_docente)
	where m.id_modulo = @id_modulo and rapd.id_ciclo_escolar = $id_ciclo_escolar;

END ;;
