CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getEscuelasUsuarios`(IN `$numero_docentes` VARCHAR(3), IN `$tipo` VARCHAR(3))
BEGIN

	
	IF($numero_docentes = "0") THEN  
		SELECT ces.clave_escuela, 
			ces.nombre_escuela,
			cne.nombre, 
			cen.nombre, 
			cm.nombre, 
			ces.nombre_localidad, 
			(SELECT COUNT(DISTINCT(id_docente)) FROM rel_docentes_escuelas AS rde2 WHERE rde2.clave_escuela = rde.clave_escuela GROUP BY clave_escuela) AS cantidad,
			ces.estatus,
            ces.clave_escuela
		FROM (cat_escuelas AS ces)
		LEFT JOIN rel_docentes_escuelas AS rde
		ON ces.clave_escuela = rde.clave_escuela
		LEFT JOIN docentes AS d
		ON rde.id_docente = d.id_docente
		INNER JOIN cat_niveles_educativos AS cne
		ON ces.id_nivel_educativo = cne.id_nivel_educativo
		INNER JOIN cat_entidades AS cen
		ON ces.id_entidad = cen.id_entidad
		INNER JOIN cat_municipios AS cm
		ON ces.id_municipio = cm.id_municipio
		WHERE ces.estatus = 1 AND 
				d.estatus = 1 AND
                
			CASE $tipo
				WHEN "1" THEN
					ces.clave_escuela like '__EJN%'   
				WHEN "2" THEN
					ces.clave_escuela like '__DJN%'  
				WHEN "3" THEN
					ces.clave_escuela like '__EML%'  
				WHEN "4" THEN
					ces.clave_escuela like '__DML%'  
				WHEN "5" THEN
					ces.clave_escuela like '__EPR%'  
				WHEN "6" THEN
					ces.clave_escuela like '__DPR%'  
				WHEN "7" THEN
					ces.clave_escuela like '__000%'  
				WHEN "8" THEN
					ces.clave_escuela like '__EES%'  
				WHEN "9" THEN
					ces.clave_escuela like '__DST%'  
				WHEN "10" THEN
					ces.clave_escuela like '__EST%'  
				WHEN "11" THEN
					ces.clave_escuela like '__ETV%' OR ces.clave_escuela like '__DTV%'  
				ELSE
					ces.clave_escuela 
			END
            
		GROUP BY ces.clave_escuela;
    ELSE
		
		SELECT ces.clave_escuela, 
			ces.nombre_escuela,
			cne.nombre, 
			cen.nombre, 
			cm.nombre, 
			ces.nombre_localidad, 
			IFNULL( (SELECT COUNT(DISTINCT(id_docente)) FROM rel_docentes_escuelas AS rde2 WHERE rde2.clave_escuela = rde.clave_escuela GROUP BY clave_escuela),0 ) AS cantidad,
			ces.estatus,
            ces.clave_escuela
		FROM (cat_escuelas AS ces)
		LEFT JOIN rel_docentes_escuelas AS rde
		ON ces.clave_escuela = rde.clave_escuela
		LEFT JOIN docentes AS d
		ON rde.id_docente = d.id_docente
		INNER JOIN cat_niveles_educativos AS cne
		ON ces.id_nivel_educativo = cne.id_nivel_educativo
		INNER JOIN cat_entidades AS cen
		ON ces.id_entidad = cen.id_entidad
		INNER JOIN cat_municipios AS cm
		ON ces.id_municipio = cm.id_municipio
		WHERE ces.estatus = 1 AND

		CASE $tipo
				WHEN "1" THEN
					ces.clave_escuela like '__EJN%'   
				WHEN "2" THEN
					ces.clave_escuela like '__DJN%'  
				WHEN "3" THEN
					ces.clave_escuela like '__EML%'  
				WHEN "4" THEN
					ces.clave_escuela like '__DML%'  
				WHEN "5" THEN
					ces.clave_escuela like '__EPR%'  
				WHEN "6" THEN
					ces.clave_escuela like '__DPR%'  
				WHEN "7" THEN
					ces.clave_escuela like '__000%'  
				WHEN "8" THEN
					ces.clave_escuela like '__EES%'  
				WHEN "9" THEN
					ces.clave_escuela like '__DST%'  
				WHEN "10" THEN
					ces.clave_escuela like '__EST%'  
				WHEN "11" THEN
					ces.clave_escuela like '__ETV%' OR ces.clave_escuela like '__DTV%'  
				ELSE
					ces.clave_escuela 
			END
            
		GROUP BY ces.clave_escuela;
    END IF;
END ;;
