CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInfoDocentesModulosRelacionados`(IN `$url` VARCHAR(250), IN `$id_docente` INT, IN $type INT, $id_ciclo_escolar int )
    NO SQL
BEGIN

	IF($type = 0) THEN
		SET @id_modulo := (SELECT id_modulo FROM rel_docentes_modulos WHERE url = $url);
	ELSE
		SET @id_modulo := (SELECT id_modulo FROM cat_modulos WHERE url = $url);
	END IF;

	SELECT DISTINCT doc.id_docente,rapd.id_modulo,doc.nombre_docente,doc.ape_paterno,doc.ape_materno,doc.nombre_usuario,esc.nombre_escuela,gra.nombre grado,gru.nombre grupo,rapd.url
	FROM docentes doc
	JOIN rel_docentes_modulos rapd ON(doc.id_docente = rapd.id_docente)
	JOIN cat_escuelas esc ON(rapd.clave_escuela = esc.clave_escuela)
	JOIN cat_grados gra ON(rapd.id_grado=gra.id_grado)
	JOIN cat_grupos gru ON(rapd.id_grupo=gru.id_grupo)
	JOIN cat_modulos m ON(rapd.id_modulo = m.id_modulo)
	WHERE rapd.id_docente = $id_docente AND rapd.id_modulo = @id_modulo  AND rapd.estatus_docente = '1' 
	AND rapd.estatus_modulo = '1' AND rapd.id_ciclo_escolar = $id_ciclo_escolar;
    
END ;;
