            (SELECT IF(EXISTS (SELECT * FROM likes_rel_docentes_modulos WHERE id_rel_modulo_docente = rdp.id_rel_modulo_docente AND id_docente = $id_usuario AND rol = $rol), "liked",""))
            else
		    (SELECT IF(EXISTS (SELECT * FROM likes_rel_docentes_modulos WHERE id_rel_modulo_docente = rdp.id_rel_modulo_docente AND id_administrador = $id_usuario AND rol = $rol), "liked",""))
            END ) AS css_likes,
			d.nombre_docente AS nombre_docente,
			d.ape_paterno AS ape_paterno_docente,
			d.ape_materno AS ape_materno_docente,
			d.nombre_usuario AS nombre_usuario,
			d.imagen AS imagen_docente, 
			m.nombre AS nombre_modulo, 
			m.descripcion,
