CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInfoPortadaProyectoDocente`(IN `$url` VARCHAR(250), IN `$id_usuario` INT, IN `$rol` CHAR(2))
    NO SQL
BEGIN
	SET @estado := (SELECT (SELECT 'Finalizado' AS estatus 
			FROM proyectos 
			WHERE curdate() > fecha_fin AND  id_proyecto = rdp.id_proyecto AND estatus = '1'
			UNION ALL 
			SELECT 'En curso' AS estatus 
			FROM proyectos 
			WHERE curdate() >= fecha_inicio AND  curdate() <= fecha_fin AND id_proyecto = rdp.id_proyecto AND estatus = '1'
			UNION ALL 
			SELECT 'Pendiente' AS estatus 
			FROM proyectos 
			WHERE curdate() < fecha_inicio AND  id_proyecto = rdp.id_proyecto AND estatus = '1')
			FROM rel_docentes_proyectos AS rdp WHERE url = $url);

	SELECT rdp.id_rel_proyecto_docente AS id_rel_proyecto_docente, 
			rdp.id_proyecto AS id_proyecto,
			rdp.url AS url_rel_proyecto, 
			rdp.estatus_docente AS rel_estatus_proyecto_docente, 
			rdp.estatus_proyecto AS rel_estatus_proyecto,
			F_getLikesRelProyectoDocente(rdp.id_rel_proyecto_docente) AS cant_likes,
            (case $rol
            when '0' then 
            (SELECT IF(EXISTS (SELECT * FROM likes_rel_docentes_proyectos WHERE id_rel_proyecto_docente = rdp.id_rel_proyecto_docente AND id_docente = $id_usuario AND rol = $rol), "liked",""))
            else
		    (SELECT IF(EXISTS (SELECT * FROM likes_rel_docentes_proyectos WHERE id_rel_proyecto_docente = rdp.id_rel_proyecto_docente AND id_administrador = $id_usuario AND rol = $rol), "liked",""))
            END ) AS css_likes,
			@estado AS rel_estatus_fecha_proyecto,
			(CASE @estado 
				WHEN "Finalizado" THEN "finalizado"
				WHEN "En curso" THEN "curso"
				WHEN "Pendiente" THEN "pendiente"
			END) AS css_estatus_proyecto,
			d.nombre_docente AS nombre_docente,
			d.ape_paterno AS ape_paterno_docente,
			d.ape_materno AS ape_materno_docente,
			d.nombre_usuario AS nombre_usuario,
			d.imagen AS imagen_docente, 
			p.nombre AS nombre_proyecto, 
			p.descripcion,
			p.fecha_inicio AS fecha_inicio,
			p.fecha_fin AS fecha_fin,
			p.imagen_portada, 
			p.url AS url_proyecto, 
			p.estatus AS estatus_proyecto, 
			ce.nombre AS nombre_ciclo_escolar, 
			e.clave_escuela,
            e.nombre_escuela, 
			g.nombre AS nombre_grado, 
			gru.nombre AS nombre_grupo,
            p.color
	FROM rel_docentes_proyectos AS rdp 
	INNER JOIN proyectos AS p 
	ON rdp.id_proyecto = p.id_proyecto 
	INNER JOIN cat_grados AS g 
	ON rdp.id_grado = g.id_grado 
	INNER JOIN cat_grupos AS gru 
	ON rdp.id_grupo = gru.id_grupo 
	INNER JOIN cat_escuelas as e 
	ON rdp.clave_escuela = e.clave_escuela 
	INNER JOIN cat_ciclos_escolares AS ce 
	ON p.id_ciclo_escolar = ce.id_ciclo_escolar
	INNER JOIN docentes AS d 
	ON rdp.id_docente = d.id_docente
	WHERE rdp.url = $url;
END ;;
