CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInfoProyecto`(IN `$id_proyecto` INT)
    NO SQL
SELECT p.id_proyecto, 
		p.nombre AS nombre_proyecto, 
        DATE_FORMAT(p.fecha_inicio_inscripcion,'%d/%m/%Y') AS fecha_inicio_inscripcion, 
        DATE_FORMAT(p.fecha_fin_inscripcion,'%d/%m/%Y') AS fecha_fin_inscripcion, 
        DATE_FORMAT(p.fecha_inicio,'%d/%m/%Y') AS fecha_inicio, 
        DATE_FORMAT(p.fecha_fin,'%d/%m/%Y') AS fecha_fin,
        CONCAT(day(p.fecha_inicio_inscripcion),' de ',F_getMesEnEspañol(month(p.fecha_inicio_inscripcion)),' de ',year(p.fecha_inicio_inscripcion)) as fecha_ini_ins_tex,
        CONCAT(day(p.fecha_fin_inscripcion),' de ',F_getMesEnEspañol(month(p.fecha_fin_inscripcion)),' de ',year(p.fecha_fin_inscripcion)) as fecha_fin_ins_tex,
        CONCAT(day(p.fecha_inicio),' de ',F_getMesEnEspañol(month(p.fecha_inicio)),' de ',year(p.fecha_inicio)) as fecha_ini_tex,
        CONCAT(day(p.fecha_fin),' de ',F_getMesEnEspañol(month(p.fecha_fin)),' de ',year(p.fecha_fin)) as fecha_fin_tex,
        ce.nombre AS nombre_ciclo_escolar, 
        DATE_FORMAT(ce.fecha_inicio,'%d/%m/%Y') AS fecha_inicio_ciclo_escolar, 
        DATE_FORMAT(ce.fecha_fin,'%d/%m/%Y') AS fecha_fin_ciclo_escolar, 
        CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador, 
        p.fecha_creacion, 
        p.descripcion, 
        p.imagen_portada, 
        if(p.estatus = '1','Activo','Inactivo') as estatus,
		(case p.estatus
			when curdate() > p.fecha_fin AND p.estatus = '1' then 'Finalizado'
            when curdate() >= p.fecha_inicio AND curdate() <= p.fecha_fin AND p.estatus = '1' then 'En curso'
            when curdate() < p.fecha_inicio AND p.estatus = '1' then 'Pendiente'
		end) as estado,
		(case p.estatus
			when curdate() > p.fecha_fin AND p.estatus = '1' then 'finalizado'
            when curdate() >= p.fecha_inicio AND curdate() <= p.fecha_fin AND p.estatus = '1' then 'curso'
            when curdate() < p.fecha_inicio AND p.estatus = '1' then 'pendiente'
		end) as css_estado, p.id_ciclo_escolar, p.color
FROM proyectos AS p
INNER JOIN administradores AS a
ON p.id_administrador = a.id_administrador
INNER JOIN cat_ciclos_escolares AS ce
ON p.id_ciclo_escolar = ce.id_ciclo_escolar
WHERE p.id_proyecto = $id_proyecto ;;
