CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInfoPublicacionProyecto`(IN `$url_proyecto` VARCHAR(100), IN `$id_publicacion_proyecto_docente` INT, IN `$id_usuario` INT, $type INT)
    NO SQL
IF($type = 1) THEN
	select distinct pp.id_publicacion_proyecto_docente, pp.publicacion,
		( select count(*) from enlaces_youtube_publicaciones where id_publicacion_proyecto_docente = pp.id_publicacion_proyecto_docente) as cantidad_videos,
	   ( select count(*) from imagenes_publicaciones where id_publicacion_proyecto_docente = pp.id_publicacion_proyecto_docente) as cantidad_fotos,
	   ( select count(*) from archivos_publicaciones where id_publicacion_proyecto_docente = pp.id_publicacion_proyecto_docente) as cantidad_archivos
	  from proyectos pro
	  join rel_docentes_proyectos rdp on (pro.id_proyecto = rdp.id_proyecto)
	  join docentes doc on (rdp.id_docente = doc.id_docente)

	  join publicaciones_proyectos pp on (rdp.id_rel_proyecto_docente = pp.id_rel_proyecto_docente)
	  where rdp.url= $url_proyecto and pp.id_publicacion_proyecto_docente= $id_publicacion_proyecto_docente and doc.id_docente = $id_usuario;
ELSE
	select distinct pp.id_publicacion_modulo_docente, pp.publicacion,
		( select count(*) from enlaces_youtube_publicaciones_mod where id_publicacion_modulo_docente = pp.id_publicacion_modulo_docente) as cantidad_videos,
	   ( select count(*) from imagenes_publicaciones_mod where id_publicacion_modulo_docente = pp.id_publicacion_modulo_docente) as cantidad_fotos,
	   ( select count(*) from archivos_publicaciones_mod where id_publicacion_modulo_docente = pp.id_publicacion_modulo_docente) as cantidad_archivos
	  from cat_modulos m
	  join rel_docentes_modulos rdp on (m.id_modulo = rdp.id_modulo)
	  join docentes doc on (rdp.id_docente = doc.id_docente)
	  join publicaciones_modulos pp on (rdp.id_rel_modulo_docente = pp.id_rel_modulo_docente)
	  where rdp.url= $url_proyecto and pp.id_publicacion_modulo_docente= $id_publicacion_proyecto_docente and doc.id_docente = $id_usuario;
END IF ;;
