CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInformacionModulo`($urlModulo varchar(50))
BEGIN
	select m.nombre,
			m.descripcion,
			m.imagen_portada,
            ci.nombre as ciclo_escolar,
			doc.nombre_docente,
			doc.ape_paterno,
            doc.ape_materno,
            doc.nombre_usuario,
            esc.nombre_escuela,
            esc.clave_escuela,
            gra.nombre grado,
            gru.nombre grupo
	from  rel_docentes_modulos rapd
	join cat_modulos m on(rapd.id_modulo = m.id_modulo)
	join cat_ciclos_escolares ci on(rapd.id_ciclo_escolar = ci.id_ciclo_escolar)
	join docentes doc on(rapd.id_docente = doc.id_docente)
	join cat_escuelas esc on(rapd.clave_escuela = esc.clave_escuela)
	join cat_grados gra on(rapd.id_grado=gra.id_grado)
	join cat_grupos gru on(rapd.id_grupo=gru.id_grupo)
	where rapd.url = $urlModulo and rapd.estatus_docente = '1' 
	and rapd.estatus_modulo = '1';
END ;;
