CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInformacionProyecto`($urlProyecto varchar(50))
BEGIN
	select pro.nombre,
			pro.descripcion,
			pro.imagen_portada,
            pro.fecha_inicio,
            pro.fecha_fin,
            ci.nombre as ciclo_escolar,
			doc.nombre_docente,
			doc.ape_paterno,
            doc.ape_materno,
            doc.nombre_usuario,
            esc.nombre_escuela,
            esc.clave_escuela,
            gra.nombre grado,
            gru.nombre grupo,
            CONCAT(day(pro.fecha_inicio),' de ',F_getMesEnEspañol(month(pro.fecha_inicio)),' de ',year(pro.fecha_inicio)) as fecha_ini_tex,
			CONCAT(day(pro.fecha_fin),' de ',F_getMesEnEspañol(month(pro.fecha_fin)),' de ',year(pro.fecha_fin)) as fecha_fin_tex,
			(case pro.estatus
				when curdate() > pro.fecha_fin AND pro.estatus = '1' then 'Finalizado'
				when curdate() >= pro.fecha_inicio AND curdate() <= pro.fecha_fin AND pro.estatus = '1' then 'En curso'
				when curdate() < pro.fecha_inicio AND pro.estatus = '1' then 'Pendiente'
			end) as estado,
			(case pro.estatus
				when curdate() > pro.fecha_fin AND pro.estatus = '1' then 'finalizado'
				when curdate() >= pro.fecha_inicio AND curdate() <= pro.fecha_fin AND pro.estatus = '1' then 'curso'
				when curdate() < pro.fecha_inicio AND pro.estatus = '1' then 'pendiente'
			end) as css_estado
	from  rel_docentes_proyectos rapd
	join proyectos pro on(rapd.id_proyecto = pro.id_proyecto)
	join cat_ciclos_escolares ci on(pro.id_ciclo_escolar = ci.id_ciclo_escolar)
	join docentes doc on(rapd.id_docente = doc.id_docente)
	join cat_escuelas esc on(rapd.clave_escuela = esc.clave_escuela)
	join cat_grados gra on(rapd.id_grado=gra.id_grado)
	join cat_grupos gru on(rapd.id_grupo=gru.id_grupo)
	where rapd.url = $urlProyecto and rapd.estatus_docente = '1' 
	and rapd.estatus_proyecto = '1';
END ;;
