CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getInfoUsuario`($email varchar(400))
BEGIN
	SELECT id_docente AS id_usuario,rol,nombre_docente AS nombre ,ape_paterno,ape_materno,email FROM docentes WHERE email = $email
		UNION ALL
	SELECT id_administrador AS id_usuario,rol,nombre AS nombre,ape_paterno,ape_materno,email FROM administradores WHERE email = $email;
END ;;
