CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getMasResultadosBusquedaPrincipal`($filtroBusqueda varchar(200), $tipoBusqueda varchar(50), $limit1 int, $limit2 int)
BEGIN
	IF($tipoBusqueda = 'proyectos') 
		THEN
			select pro.imagen_portada,pro.nombre,pro.url,pro.descripcion
			from proyectos pro
			where pro.nombre like CONCAT ('%',$filtroBusqueda, '%') and pro.estatus = '1'
             and pro.id_proyecto = (select max(pro2.id_proyecto) from proyectos pro2
								where pro2.renovar = pro.renovar)
			limit  $limit1, $limit2 ;
        
        
			
		END IF;
		IF($tipoBusqueda = 'modulos') 
		THEN 
			select nombre, descripcion, imagen_portada, url from cat_modulos where estatus = '1' and
			nombre like CONCAT ('%', $filtroBusqueda, '%')
			limit  $limit1, $limit2 ;
		END IF;
		IF($tipoBusqueda = 'docentes') 
		THEN 
			select distinct imagen,CONCAT (nombre_docente,' ',ape_paterno,' ',ape_materno) as nombre_docente,nombre_usuario,ent.nombre as entidad,
			mun.nombre as municipio, doc.nombre_localidad as localidad,
			(select cat1.clave_escuela
			from docentes doc1
			join rel_docentes_escuelas rde1 on (rde1.id_docente = doc1.id_docente)
			join cat_escuelas cat1 on (cat1.clave_escuela = rde1.clave_escuela)
			 where doc1.id_docente = doc.id_docente
			 limit 0 ,1 ) as clave_escuela, 
			 
			 (select cat1.nombre_escuela
			from docentes doc1
			join rel_docentes_escuelas rde1 on (rde1.id_docente = doc1.id_docente)
			join cat_escuelas cat1 on (cat1.clave_escuela = rde1.clave_escuela)
			 where doc1.id_docente = doc.id_docente
			 limit 0 ,1 ) as nombre_escuela,
			 
			 (select count( distinct cat1.clave_escuela) 
			 from docentes doc1
			join rel_docentes_escuelas rde1 on (rde1.id_docente = doc1.id_docente)
			join cat_escuelas cat1 on (cat1.clave_escuela = rde1.clave_escuela)
			 where doc1.id_docente = doc.id_docente ) as cantidad_escuelas 
						from docentes doc
						join cat_municipios mun on (doc.id_municipio = mun.id_municipio and doc.id_entidad = mun.id_entidad)
						join cat_entidades ent on (doc.id_municipio = mun.id_municipio and ent.id_entidad = mun.id_entidad)
						where CONCAT_WS(' ',nombre_docente,ape_paterno,ape_materno) like CONCAT ('%',$filtroBusqueda, '%') or
								CONCAT_WS(' ',nombre_docente,ape_materno,ape_paterno) like CONCAT ('%',$filtroBusqueda, '%') or
								CONCAT_WS(' ',ape_paterno,ape_materno,nombre_docente) like CONCAT ('%',$filtroBusqueda, '%') or
								CONCAT_WS(' ',ape_materno,ape_paterno,nombre_docente) like CONCAT ('%',$filtroBusqueda, '%') 
								and estatus = '1'
						limit  $limit1, $limit2;
		END IF;
		IF($tipoBusqueda = 'escuelas') 
		THEN 
			set @index_separacion = (select locate(',',$filtroBusqueda));
			if( @index_separacion > 0 )  
			THEN
				 set @nombre_escuela = (select trim(left($filtroBusqueda,(@index_separacion-1))));
				 
				 				 
				 set @municipio = (select trim(substring($filtroBusqueda,(@index_separacion+1),length($filtroBusqueda))));

				select distinct nombre_escuela, clave_escuela, ent.nombre as entidad, mun.nombre as municipio, nombre_localidad as localidad, niv.nombre as nivel_educativo
				from cat_escuelas esc
                join cat_entidades ent on (ent.id_entidad = esc.id_entidad)
				join cat_municipios mun on (esc.id_municipio = mun.id_municipio and esc.id_entidad = mun.id_entidad)
				join  cat_niveles_educativos niv on(esc.id_nivel_educativo = niv.id_nivel_educativo)
				where nombre_escuela like CONCAT ('%',@nombre_escuela, '%')
				and ( 	
						mun.nombre like CONCAT ('%',@municipio, '%') 
						or nombre_localidad like CONCAT ('%',@municipio, '%') 
					)
				and estatus = '1' 
				and esc.id_entidad = 30
				limit $limit1 ,$limit2;
			 else
					select distinct nombre_escuela, clave_escuela, ent.nombre as entidad, mun.nombre as municipio, nombre_localidad as localidad, niv.nombre as nivel_educativo
					from cat_escuelas esc
                    join cat_entidades ent on (ent.id_entidad = esc.id_entidad)
					join cat_municipios mun on (esc.id_municipio = mun.id_municipio and esc.id_entidad = mun.id_entidad)
					join  cat_niveles_educativos niv on(esc.id_nivel_educativo = niv.id_nivel_educativo)
					where nombre_escuela like CONCAT ('%',$filtroBusqueda, '%')
					or clave_escuela like CONCAT ('%', $filtroBusqueda, '%')
					and estatus = '1' 
					and esc.id_entidad = 30
					limit $limit1 ,$limit2;
			
			 end if;
		END IF;
END ;;
DELIMITER ;
