CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getMessages`(IN $id_usuario_propietario BIGINT, IN $rol_propietario CHAR(2), IN $usuario_consultar VARCHAR(50))
BEGIN
	DROP TEMPORARY TABLE IF EXISTS TMP_message;
	IF EXISTS(SELECT * FROM docentes WHERE nombre_usuario = $usuario_consultar)THEN
    BEGIN
		SELECT id_docente INTO @id_usuario FROM docentes WHERE nombre_usuario = $usuario_consultar;
        IF($rol_propietario = "0") THEN
			BEGIN
				CREATE TEMPORARY TABLE IF NOT EXISTS TMP_message AS (
					SELECT id_comentario,
							comentario,
							IF(id_docente_emisor = $id_usuario_propietario,"env","rec") as css_mensaje,
                            fecha_comentario,
                            CONCAT(DATE_FORMAT(fecha_comentario, '%l'),':',DATE_FORMAT(fecha_comentario, '%i'),' ',DATE_FORMAT(fecha_comentario, '%p')) AS hora,
                            F_getInfoFechaChat(fecha_comentario) as dia,
							visto_receptor
					FROM chat_comentarios
					WHERE (id_docente_emisor = $id_usuario_propietario AND id_docente_receptor = @id_usuario) OR (id_docente_emisor = @id_usuario AND id_docente_receptor = $id_usuario_propietario)
                    ORDER BY fecha_comentario ASC
				);
                
                IF EXISTS(SELECT * FROM TMP_message) THEN
					SELECT * FROM TMP_message;
                ELSE 
                    SELECT "nuevoChat" as mensaje, 
						nombre_docente,
						ape_paterno,
						ape_materno,
						nombre_usuario,
						imagen,
						rol
					FROM docentes
                    WHERE id_docente = @id_usuario;
