                UPDATE chat_comentarios
				SET visto_receptor = 0
				WHERE (id_docente_emisor = @id_usuario AND id_docente_receptor = $id_usuario_propietario) AND visto_receptor = 1;
                
			END;
        ELSE
			BEGIN
				CREATE TEMPORARY TABLE IF NOT EXISTS TMP_message AS (
					SELECT id_comentario,
							comentario,
							IF(id_administrador_emisor = $id_usuario_propietario,"env","rec") as css_mensaje,
