CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getModulos`()
    NO SQL
BEGIN
	SELECT m.id_modulo, m.nombre AS nombre_modulo,
	CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador,
    DATE_FORMAT(m.fecha_creacion,'%d/%m/%Y'), m.descripcion, m.imagen_portada, 
