CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getModulosDocenteFiltroPerfil`(IN $nombre_usuario VARCHAR(50),  $id_ciclo_escolar int)
BEGIN
	set @id_docente = (select id_docente from docentes where nombre_usuario = $nombre_usuario);

	SELECT rdp.id_rel_modulo_docente, 
			rdp.id_modulo, 
			rdp.url AS url_rel_modulo, 
			rdp.estatus_docente AS rel_estatus_docente, 
			rdp.estatus_modulo AS rel_estatus_modulo, 
			d.nombre_usuario AS nombre_usuario, 
			m.nombre AS nombre_modulo, 
			m.descripcion, 
			m.imagen_portada, 
			m.url AS url_modulo, 
			m.estatus AS estatus_modulo, 
			ce.nombre AS nombre_ciclo_escolar,
			e.nombre_escuela, 
			g.nombre AS nombre_grado, 
			gru.nombre AS nombre_grupo 
	FROM rel_docentes_modulos AS rdp 
	INNER JOIN cat_modulos AS m 
	ON rdp.id_modulo = m.id_modulo

	INNER JOIN cat_grados AS g 
	ON rdp.id_grado = g.id_grado 
	INNER JOIN cat_grupos AS gru 
	ON rdp.id_grupo = gru.id_grupo 
	INNER JOIN cat_escuelas as e 
	ON rdp.clave_escuela = e.clave_escuela

	INNER JOIN cat_ciclos_escolares AS ce 
	ON rdp.id_ciclo_escolar = ce.id_ciclo_escolar
	INNER JOIN docentes AS d 
	ON rdp.id_docente = d.id_docente
	WHERE rdp.id_docente = @id_docente
	AND rdp.id_ciclo_escolar = $id_ciclo_escolar AND m.estatus = '1'
	ORDER BY rdp.id_modulo ASC;


