		m.fecha_creacion, m.descripcion, m.imagen_portada, 
        (SELECT count(rapd.id_modulo) 
			FROM rel_docentes_modulos rapd 
            WHERE m.id_modulo = rapd.id_modulo 
				AND rapd.estatus_docente = 1 
                AND rapd.estatus_modulo = 1) AS numero_participantes,
		m.url, m.estatus
		FROM cat_modulos AS m
		JOIN administradores AS a ON m.id_administrador = a.id_administrador
		WHERE m.estatus = 1  		and  m.nombre like CONCAT ('%', $busqueda_nombre_modulo, '%'); 	end if;
