CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getProyectosCicloEscolarActual`()
BEGIN
	IF EXISTS (SELECT * FROM cat_ciclos_escolares WHERE estatus = '1') THEN
        
        SELECT p.id_proyecto, p.nombre AS nombre_proyecto, 
		DATE_FORMAT(p.fecha_inicio_inscripcion,'%d/%m/%Y') AS fecha_inicio_inscripcion,
		DATE_FORMAT(p.fecha_fin_inscripcion,'%d/%m/%Y') AS fecha_fin_inscripcion,
		DATE_FORMAT(p.fecha_inicio,'%d/%m/%Y') AS fecha_inicio, 
		DATE_FORMAT(p.fecha_fin,'%d/%m/%Y') AS fecha_fin, 
		ce.nombre AS nombre_ciclo_escolar,
		CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador,
		DATE_FORMAT(p.fecha_creacion,'%d/%m/%Y'), p.descripcion, p.imagen_portada, 
		(
		SELECT count(rapd.id_proyecto) 
		FROM rel_docentes_proyectos rapd
		WHERE p.id_proyecto = rapd.id_proyecto AND rapd.estatus_docente = 1 AND rapd.estatus_proyecto = 1
		)
		AS numero_participantes,
		p.url AS link,
		'Activo' AS estatus
		FROM proyectos AS p
		JOIN administradores AS a ON p.id_administrador = a.id_administrador
		JOIN cat_ciclos_escolares AS ce ON p.id_ciclo_escolar = ce.id_ciclo_escolar
		WHERE p.id_ciclo_escolar = (SELECT id_ciclo_escolar FROM cat_ciclos_escolares WHERE estatus = '1') AND p.estatus = '1';
        
    END IF;
END ;;
