CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getProyectosDocente`($id_docente int)
BEGIN

set @id_ciclo_escolar_actual = (SELECT id_ciclo_escolar FROM cat_ciclos_escolares ORDER BY estatus DESC, id_ciclo_escolar DESC limit 0,1);

	SELECT rdp.id_rel_proyecto_docente, rdp.id_proyecto, rdp.url AS url_rel_proyecto, rdp.estatus_docente AS rel_estatus_docente, rdp.estatus_proyecto AS rel_estatus_proyecto, d.nombre_usuario AS nombre_usuario, p.nombre AS nombre_proyecto, p.descripcion, p.imagen_portada, p.url AS url_proyecto, p.estatus AS estatus_proyecto, ce.nombre AS nombre_ciclo_escolar, e.nombre_escuela, g.nombre AS nombre_grado, gru.nombre AS nombre_grupo 
	FROM rel_docentes_proyectos AS rdp 
	INNER JOIN proyectos AS p 
	ON rdp.id_proyecto = p.id_proyecto 
	INNER JOIN cat_grados AS g 
	ON rdp.id_grado = g.id_grado 
	INNER JOIN cat_grupos AS gru 
	ON rdp.id_grupo = gru.id_grupo 
	INNER JOIN cat_escuelas as e 
	ON rdp.clave_escuela = e.clave_escuela 
	INNER JOIN cat_ciclos_escolares AS ce 
	ON p.id_ciclo_escolar = ce.id_ciclo_escolar
	INNER JOIN docentes AS d 
	ON rdp.id_docente = d.id_docente
	WHERE rdp.id_docente = $id_docente
	AND p.estatus= '1' 	AND e.estatus = '1'  	AND d.estatus = '1'     AND p.id_ciclo_escolar = @id_ciclo_escolar_actual 
	ORDER BY rdp.id_proyecto ASC;
END ;;
DELIMITER ;
