CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getProyectosDocentesRelacionados`(IN `$url` VARCHAR(250), IN `$id_docente` INT)
    NO SQL
select distinct doc.id_docente,rapd.id_proyecto,doc.nombre_docente,doc.ape_paterno,esc.nombre_escuela,gra.nombre grado,gru.nombre grupo,rapd.url
from docentes doc
join rel_docentes_proyectos rapd on(doc.id_docente = rapd.id_docente)
join cat_escuelas esc on(rapd.clave_escuela = esc.clave_escuela)
join cat_grados gra on(rapd.id_grado=gra.id_grado)
join cat_grupos gru on(rapd.id_grupo=gru.id_grupo)
join proyectos pro on(rapd.id_proyecto = pro.id_proyecto)
where rapd.id_docente = $id_docente and rapd.estatus_docente = '1' 
and rapd.estatus_proyecto = '1' and rapd.url != $url ;;
