CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_getProyectosFiltro`($id_carpeta_proyecto int , $filtro_proyectos varchar(100), $busqueda_nombre_proyecto varchar(100))
BEGIN
	if($filtro_proyectos='Todos') then
    
		SELECT p.id_proyecto, p.nombre AS nombre_proyecto, 
		DATE_FORMAT(p.fecha_inicio_inscripcion,'%d/%m/%Y') AS fecha_inicio_inscripcion,
		DATE_FORMAT(p.fecha_fin_inscripcion,'%d/%m/%Y') AS fecha_fin_inscripcion,
		DATE_FORMAT(p.fecha_inicio,'%d/%m/%Y') AS fecha_inicio, 
		DATE_FORMAT(p.fecha_fin,'%d/%m/%Y') AS fecha_fin, 
		ce.nombre AS nombre_ciclo_escolar,
		CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador,
		p.fecha_creacion, p.descripcion, p.imagen_portada, (SELECT count(rapd.id_proyecto) FROM rel_docentes_proyectos rapd WHERE p.id_proyecto = rapd.id_proyecto AND rapd.estatus_docente = 1 AND rapd.estatus_proyecto = 1) AS numero_participantes,
		p.url AS link, if(p.estatus = '1','Activo','Inactivo') AS estatus
		FROM proyectos AS p
		JOIN administradores AS a ON p.id_administrador = a.id_administrador
		JOIN cat_ciclos_escolares AS ce ON p.id_ciclo_escolar = ce.id_ciclo_escolar
		WHERE p.estatus = 1  		AND ( CURDATE() BETWEEN p.fecha_inicio_inscripcion  AND p.fecha_fin_inscripcion )
				AND p.id_ciclo_escolar = (SELECT id_ciclo_escolar FROM cat_ciclos_escolares
		WHERE estatus = '1');
    
    elseif ($filtro_proyectos='Titulo') then
    
		SELECT p.id_proyecto, p.nombre AS nombre_proyecto, 
		DATE_FORMAT(p.fecha_inicio_inscripcion,'%d/%m/%Y') AS fecha_inicio_inscripcion,
		DATE_FORMAT(p.fecha_fin_inscripcion,'%d/%m/%Y') AS fecha_fin_inscripcion,
		DATE_FORMAT(p.fecha_inicio,'%d/%m/%Y') AS fecha_inicio, 
		DATE_FORMAT(p.fecha_fin,'%d/%m/%Y') AS fecha_fin, 
		ce.nombre AS nombre_ciclo_escolar,
		CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador,
		p.fecha_creacion, p.descripcion, p.imagen_portada, (SELECT count(rapd.id_proyecto) FROM rel_docentes_proyectos rapd WHERE p.id_proyecto = rapd.id_proyecto AND rapd.estatus_docente = 1 AND rapd.estatus_proyecto = 1) AS numero_participantes,
		p.url AS link, if(p.estatus = '1','Activo','Inactivo') AS estatus
		FROM proyectos AS p
		JOIN rel_carpeta_proyectos AS rcp ON rcp.id_proyecto = p.id_proyecto
		JOIN cat_carpetas AS cat ON (rcp.id_carpeta = cat.id_carpeta)
		JOIN administradores AS a ON p.id_administrador = a.id_administrador
		JOIN cat_ciclos_escolares AS ce ON p.id_ciclo_escolar = ce.id_ciclo_escolar
		WHERE p.estatus = 1  		AND  cat.id_carpeta=$id_carpeta_proyecto 
				AND ( CURDATE() BETWEEN p.fecha_inicio_inscripcion  AND p.fecha_fin_inscripcion ) 
				AND p.id_ciclo_escolar = (SELECT id_ciclo_escolar FROM cat_ciclos_escolares
		WHERE estatus = '1');
	
    elseif ($filtro_proyectos = 'Nombre') then

		SELECT p.id_proyecto, p.nombre AS nombre_proyecto, 
		DATE_FORMAT(p.fecha_inicio_inscripcion,'%d/%m/%Y') AS fecha_inicio_inscripcion,
		DATE_FORMAT(p.fecha_fin_inscripcion,'%d/%m/%Y') AS fecha_fin_inscripcion,
		DATE_FORMAT(p.fecha_inicio,'%d/%m/%Y') AS fecha_inicio, 
		DATE_FORMAT(p.fecha_fin,'%d/%m/%Y') AS fecha_fin, 
		ce.nombre AS nombre_ciclo_escolar,
		CONCAT(a.nombre,' ',a.ape_paterno,' ',a.ape_materno) AS nombre_administrador,
		p.fecha_creacion, p.descripcion, p.imagen_portada, 
		(SELECT count(rapd.id_proyecto) FROM rel_docentes_proyectos rapd WHERE p.id_proyecto = rapd.id_proyecto AND rapd.estatus_docente = 1 AND rapd.estatus_proyecto = 1)AS numero_participantes,
		p.url AS link,
		if(p.estatus = '1','Activo','Inactivo') AS estatus
		FROM proyectos AS p
		JOIN administradores AS a ON p.id_administrador = a.id_administrador
		JOIN cat_ciclos_escolares AS ce ON p.id_ciclo_escolar = ce.id_ciclo_escolar
		WHERE p.estatus = 1  		AND ( CURDATE() BETWEEN p.fecha_inicio_inscripcion  AND p.fecha_fin_inscripcion ) 
				and  p.nombre like CONCAT ('%', $busqueda_nombre_proyecto, '%') 		AND p.id_ciclo_escolar = (SELECT id_ciclo_escolar FROM cat_ciclos_escolares
		WHERE estatus = '1'); 	end if;
END ;;
DELIMITER ;
