CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_insertComentariosPublicaciones`(IN `$url_docente_proyecto` VARCHAR(150), $id_publicacion_proyecto_docente BIGINT, $comentario text,$id_usuario int, $rol varchar(2), $type INT)
BEGIN

IF($type = "1") THEN
	if exists(SELECT * FROM publicaciones_proyectos AS p INNER JOIN rel_docentes_proyectos AS r ON p.id_rel_proyecto_docente = r.id_rel_proyecto_docente WHERE r.url = $url_docente_proyecto AND p.id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente) then
		if ($rol = '0') then
			insert into comentarios_publicaciones values (null,$id_publicacion_proyecto_docente,$comentario,$id_usuario,null,$rol,NOW());

			select id_comentario_publicacion,id_publicacion_proyecto_docente,comentario_publicacion,
			F_getInfoFechaPublicacion(cp.fecha_comentario) as fecha_publicacion ,nombre_docente as nombre, ape_paterno , ape_materno,imagen,
			doc.id_docente as id_usuario,doc.rol,fecha_comentario,nombre_usuario,
			F_getInfoFechaPublicacion(fecha_comentario) as fecha_publicacion ,
			F_getInfoFechaCompletaEnEspanol(fecha_comentario) as fecha_completa,
			F_getMinutosFaltantesComentarios(id_comentario_publicacion,'1') as tiempo
			from comentarios_publicaciones cp
			join docentes doc on (cp.id_docente = doc.id_docente)
			where  id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente and  doc.id_docente = $id_usuario and doc.rol= $rol and estatus = '1'
			and id_comentario_publicacion = (
			select max(id_comentario_publicacion) 
			from comentarios_publicaciones where id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente
			);

		else 
			insert into comentarios_publicaciones values (null,$id_publicacion_proyecto_docente,$comentario,null,$id_usuario,$rol,NOW());

			select id_comentario_publicacion,id_publicacion_proyecto_docente,comentario_publicacion,
			F_getInfoFechaPublicacion(cp.fecha_comentario) as fecha_publicacion ,nombre as nombre, ape_paterno , ape_materno,imagen,
			adm.id_administrador as id_usuario,adm.rol,fecha_comentario,nombre_usuario,
			F_getInfoFechaPublicacion(fecha_comentario) as fecha_publicacion ,
			F_getInfoFechaCompletaEnEspanol(fecha_comentario) as fecha_completa,
			F_getMinutosFaltantesComentarios(id_comentario_publicacion,'1') as tiempo
			from comentarios_publicaciones cp
			join administradores adm on (cp.id_administrador = adm.id_administrador)
			where  id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente and  adm.id_administrador = $id_usuario and adm.rol= $rol and estatus = '1'
			and id_comentario_publicacion = (
			select max(id_comentario_publicacion) 
			from comentarios_publicaciones where id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente
			);
		end if;
	else
		select 'error' as mensaje;
	end if;
ELSE
	if exists(SELECT * FROM publicaciones_modulos AS p INNER JOIN rel_docentes_modulos AS r ON p.id_rel_modulo_docente = r.id_rel_modulo_docente WHERE r.url = $url_docente_proyecto AND p.id_publicacion_modulo_docente = $id_publicacion_proyecto_docente) then
		if ($rol = '0') then
			insert into comentarios_publicaciones_mod values (null,$id_publicacion_proyecto_docente,$comentario,$id_usuario,null,$rol,NOW());

			select id_comentario_publicacion,id_publicacion_modulo_docente,comentario_publicacion,
			F_getInfoFechaPublicacion(cp.fecha_comentario) as fecha_publicacion ,nombre_docente as nombre, ape_paterno , ape_materno,imagen,
			doc.id_docente as id_usuario,doc.rol,fecha_comentario,nombre_usuario,
			F_getInfoFechaPublicacion(fecha_comentario) as fecha_publicacion ,
			F_getInfoFechaCompletaEnEspanol(fecha_comentario) as fecha_completa,
			F_getMinutosFaltantesComentarios(id_comentario_publicacion,'0') as tiempo
			from comentarios_publicaciones_mod cp
			join docentes doc on (cp.id_docente = doc.id_docente)
			where  id_publicacion_modulo_docente = $id_publicacion_proyecto_docente and  doc.id_docente = $id_usuario and doc.rol= $rol and estatus = '1'
			and id_comentario_publicacion = (
			select max(id_comentario_publicacion) 
			from comentarios_publicaciones_mod where id_publicacion_modulo_docente = $id_publicacion_proyecto_docente
			);

		else 
			insert into comentarios_publicaciones_mod values (null,$id_publicacion_proyecto_docente,$comentario,null,$id_usuario,$rol,NOW());

			select id_comentario_publicacion,id_publicacion_modulo_docente,comentario_publicacion,
			F_getInfoFechaPublicacion(cp.fecha_comentario) as fecha_publicacion ,nombre as nombre, ape_paterno , ape_materno,imagen,
			adm.id_administrador as id_usuario,adm.rol,fecha_comentario,nombre_usuario,
			F_getInfoFechaPublicacion(fecha_comentario) as fecha_publicacion ,
			F_getInfoFechaCompletaEnEspanol(fecha_comentario) as fecha_completa,
			F_getMinutosFaltantesComentarios(id_comentario_publicacion,'0') as tiempo
			from comentarios_publicaciones_mod cp
			join administradores adm on (cp.id_administrador = adm.id_administrador)
			where  id_publicacion_modulo_docente = $id_publicacion_proyecto_docente and  adm.id_administrador = $id_usuario and adm.rol= $rol and estatus = '1'
			and id_comentario_publicacion = (
			select max(id_comentario_publicacion) 
			from comentarios_publicaciones_mod where id_publicacion_modulo_docente = $id_publicacion_proyecto_docente
			);
		end if;
	else
		select 'error' as mensaje;
	end if;
END IF;
END ;;
