CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_insertMensaje`(IN $id_emisor BIGINT, IN $rol_emisor CHAR(2), IN $nombre_usuario VARCHAR(255), IN $mensaje TEXT)
BEGIN
	IF EXISTS(SELECT * FROM administradores WHERE nombre_usuario = $nombre_usuario) THEN
		BEGIN
			
            SELECT id_administrador, rol INTO @id_receptor, @rol_receptor FROM administradores WHERE nombre_usuario = $nombre_usuario;
            
            IF($id_emisor > @id_receptor) THEN
				SELECT CONCAT(@id_receptor,",",$id_emisor,"-",@rol_receptor,",",$rol_emisor) INTO @chat;
            ELSEIF($id_emisor < @id_receptor) THEN
				SELECT CONCAT($id_emisor,",",@id_receptor,"-",$rol_emisor,",",@rol_receptor) INTO @chat;
            ELSE
				IF($rol_emisor > @rol_receptor) THEN
					SELECT CONCAT(@id_receptor,",",$id_emisor,"-",@rol_receptor,",",$rol_emisor) INTO @chat;
				ELSE
					SELECT CONCAT($id_emisor,",",@id_receptor,"-",$rol_emisor,",",@rol_receptor) INTO @chat;
                END IF;
            END IF;
            
            SELECT F_getInfoFechaChat(MAX(fecha_comentario)) INTO @last_date
            FROM `chat_comentarios` 
            WHERE chat = @chat 
            GROUP BY chat;
            
            IF($rol_emisor = "0") THEN
				INSERT INTO chat_comentarios VALUES(null, $mensaje, $id_emisor, null, $rol_emisor, null, @id_receptor, @rol_receptor, NOW(), 1, 1, 1, 1, null, @chat);
            ELSE
				INSERT INTO chat_comentarios VALUES(null, $mensaje, null, $id_emisor, $rol_emisor, null, @id_receptor, @rol_receptor, NOW(), 1, 1, 1, 1, null, @chat);
            END IF;
            
		END;
    ELSE
		BEGIN
			
            SELECT id_docente, rol INTO @id_receptor, @rol_receptor FROM docentes WHERE nombre_usuario = $nombre_usuario;
            
            IF($id_emisor > @id_receptor) THEN
				SELECT CONCAT(@id_receptor,",",$id_emisor,"-",@rol_receptor,",",$rol_emisor) INTO @chat;
            ELSEIF($id_emisor < @id_receptor) THEN
				SELECT CONCAT($id_emisor,",",@id_receptor,"-",$rol_emisor,",",@rol_receptor) INTO @chat;
            ELSE
				IF($rol_emisor > @rol_receptor) THEN
					SELECT CONCAT(@id_receptor,",",$id_emisor,"-",@rol_receptor,",",$rol_emisor) INTO @chat;
				ELSE
					SELECT CONCAT($id_emisor,",",@id_receptor,"-",$rol_emisor,",",@rol_receptor) INTO @chat;
                END IF;
            END IF;
            
            SELECT F_getInfoFechaChat(MAX(fecha_comentario)) INTO @last_date
            FROM `chat_comentarios` 
            WHERE chat = @chat 
            GROUP BY chat;
            
            IF($rol_emisor = "0") THEN
				INSERT INTO chat_comentarios VALUES(null, $mensaje, $id_emisor, null, $rol_emisor, @id_receptor, null, @rol_receptor, NOW(), 1, 1, 1, 1, null, @chat);
            ELSE
				INSERT INTO chat_comentarios VALUES(null, $mensaje, null, $id_emisor, $rol_emisor, @id_receptor, null, @rol_receptor, NOW(), 1, 1, 1, 1, null, @chat);
            END IF;
            
		END;
	END IF;
    
	SELECT id_comentario,
			comentario,
			"env" as css_mensaje,
			CONCAT(DATE_FORMAT(fecha_comentario, '%l'),':',DATE_FORMAT(fecha_comentario, '%i'),' ',DATE_FORMAT(fecha_comentario, '%p')) AS fecha_comentario,
			F_getInfoFechaChat(fecha_comentario) as dia,
            @last_date as ultimo_dia_mensaje,
			visto_receptor
	FROM chat_comentarios
	WHERE id_comentario = LAST_INSERT_ID();
    
END ;;
