	IF EXISTS(SELECT * FROM publicaciones_proyectos AS pp INNER JOIN rel_docentes_proyectos AS rdp ON pp.id_rel_proyecto_docente = rdp.id_rel_proyecto_docente WHERE pp.id_publicacion_proyecto_docente = $id_publicacion AND rdp.id_docente = $id_docente AND rdp.url = $url_docente_proyecto) THEN
