			INNER JOIN rel_docentes_modulos AS rdp 
			ON pp.id_rel_modulo_docente = rdp.id_rel_modulo_docente 
			WHERE pp.id_publicacion_modulo_docente = $id_publicacion AND rdp.id_docente = $id_docente AND rdp.url = $url_docente_proyecto) THEN
		UPDATE publicaciones_modulos AS pp
		INNER JOIN rel_docentes_modulos AS rdp 
		ON pp.id_rel_modulo_docente = rdp.id_rel_modulo_docente 
		SET pp.publicacion = $publicacion
		WHERE pp.id_publicacion_modulo_docente = $id_publicacion AND rdp.id_docente = $id_docente AND rdp.url = $url_docente_proyecto;

		SELECT "1" AS propietario;
