CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_verifyComentariosPublicacion`($id_comentario_publicacion BIGINT, $id_publicacion_proyecto_docente BIGINT, $url_proyecto varchar(50), $id_usuario int ,$rol varchar(2), $type INT)
BEGIN
	IF($type = "1") THEN
		if exists ( select * from  rel_docentes_proyectos rdp
		join publicaciones_proyectos pp on(pp.id_rel_proyecto_docente =  rdp.id_rel_proyecto_docente)
		join comentarios_publicaciones cp on (pp.id_publicacion_proyecto_docente = cp.id_publicacion_proyecto_docente)
		where cp.id_publicacion_proyecto_docente = $id_publicacion_proyecto_docente and cp.id_comentario_publicacion = $id_comentario_publicacion and rdp.url = $url_proyecto and rol = $rol 
		and (case 
			when $rol != 0 then cp.id_administrador = $id_usuario 
			else cp.id_docente = $id_usuario end)
		) then
			select '1' as mensaje; 
		else
			select '0' as mensaje; 
		end if;
	ELSE
		if exists ( select * from  rel_docentes_modulos rdp
		join publicaciones_modulos pp on(pp.id_rel_modulo_docente =  rdp.id_rel_modulo_docente)
		join comentarios_publicaciones_mod cp on (pp.id_publicacion_modulo_docente = cp.id_publicacion_modulo_docente)
