CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_verifyUsuarioActivo`($rol varchar(10) , $id_usuario bigint)
BEGIN
	IF($rol = '1') THEN
		IF EXISTS (SELECT * FROM administradores WHERE estatus = '1' AND ( rol = $rol OR rol = $rol ) AND id_administrador = $id_usuario ) THEN 
				SELECT  '1' AS activo;
		ELSE
				SELECT '0' AS activo;
		END IF;
	ELSE IF($rol = '0') THEN
		IF EXISTS (SELECT doc.*  FROM docentes doc WHERE doc.estatus = '1' AND doc.rol =  $rol  AND doc.id_docente = $id_usuario ) THEN
			IF EXISTS (SELECT doc.* FROM docentes doc JOIN rel_docentes_escuelas rde ON (doc.id_docente = rde.id_docente) JOIN cat_escuelas esc ON (rde.clave_escuela = esc.clave_escuela) WHERE doc.rol =  $rol AND esc.estatus = '1'  AND doc.id_docente = $id_usuario ) THEN
				SELECT '1' AS activo;
			ELSE
				SELECT '2' AS activo;
			END IF;
		ELSE
			IF EXISTS (SELECT doc.*  FROM docentes doc JOIN rel_docentes_escuelas rde ON (doc.id_docente = rde.id_docente) JOIN cat_escuelas esc ON (rde.clave_escuela = esc.clave_escuela) WHERE doc.rol =  $rol AND esc.estatus = '1'  AND doc.id_docente = $id_usuario ) THEN
				SELECT '0' AS activo;
			ELSE
				SELECT '3' AS activo;
				END IF;
		END IF;
	ELSE IF($rol = '3') THEN
		IF EXISTS (SELECT * FROM asesores WHERE estatus = '1' AND ( rol = $rol OR rol = $rol ) AND id_asesor = $id_usuario ) THEN 
				SELECT  '1' AS activo;
		ELSE
				SELECT '0' AS activo;
		END IF;
	ELSE IF($rol = '4') THEN
		IF EXISTS (SELECT * FROM jueces WHERE estatus = '1' AND ( rol = $rol OR rol = $rol ) AND id_juez = $id_usuario ) THEN 
				SELECT  '1' AS activo;
		ELSE
				SELECT '0' AS activo;
		END IF;
	END IF;
	END IF;
	END IF;
    END IF;
END ;;
